mapper.select:
SELECT 

FROM t_device a

WHERE a.id = #{id}

SELECT 

FROM t_device a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.brand LIKE 
			'%'||#{brand}||'%'
			'%'+#{brand}+'%'
			concat('%',#{brand},'%')


		AND a.lab_id = #{labId}


		AND a.ip = #{ip}


		AND a.completed = #{completed}


		AND a.address LIKE 
			'%'||#{address}||'%'
			'%'+#{address}+'%'
			concat('%',#{address},'%')




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM t_device a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE t_device SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
