mapper.select:
SELECT 

FROM t_memory a

WHERE a.id = #{id}

SELECT 

FROM t_memory a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT

FROM t_memory a


	a.del_flag = 0 AND  a.device_id = #{device_id}

SELECT 

FROM t_memory a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE t_memory SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
